import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import UserEdit from "./UserEdit";

const UserList = () => {
    const [users,setUsers] = useState(null)
    const [editShow,setEditShow] = useState(false)
    const [editData,setEditData] = useState('')


    useEffect(()=>{
        fetch('http://localhost:8080/users').then((result)=>{
            return result.json()
        }).then((response)=>{
            setUsers(response)
        }).catch((err)=>{
            console.log(err.message)
        })
    },[])

    const userEdit = (id) => {
        setEditShow(true)
        fetch('http://localhost:8080/users/'+id).then((result)=>{
            return result.json()
        }).then((response)=>{
            console.log(response)
            setEditData(response)
        }).catch((err)=>{
            console.log(err.message)
        })
        
    }

    const closeEdit = () => {
        setEditShow(false)
    }

    return (
        <div className="container">
            <div className="card" >
                <div className="card-body">
                    <h5 className="card-title">User's List</h5>
                    <Link to='/user/create' className="btn btn-success" style={{float:'right',margin:'5px'}}>Add User</Link>
                    <table className="table table-striped">
                        <thead className="bg-dark text-white">
                            <tr>
                            <th>S.No</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Phone No</th>
                            <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                              users &&  users.map((item,index)=>(
                                <tr key={index}>
                                    <td>{index+1}</td>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.phone}</td>
                                    <td>
                                        <button className="btn btn-sm btn-primary" onClick={()=>userEdit(item.id)}> <i className="fa fa-edit" /></button>
                                        <button className="btn btn-sm btn-danger"><i className="fa fa-trash" /></button>
                                        <UserEdit editShow = {editShow} editData ={editData} closeEdit={closeEdit} />

                                    </td>
                                </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div>
    )
}

export default UserList;
