import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Swal from "sweetalert2";

const UserList = () => {
    const [users,setUsers] = useState(null)
    // User is currently on this page
const [currentPage, setCurrentPage] = useState(1);
// No of Records to be displayed on each page   
const [recordsPerPage] = useState(10);


    useEffect(()=>{
        fetch('http://localhost:8080/users').then((result)=>{
            return result.json()
        }).then((response)=>{
            setUsers(response)
        }).catch((err)=>{
            console.log(err.message)
        })
    },[])

    const deleteUser = (userId) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "Do you want to delete this user?",
            icon: 'question',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
          }).then((result) => {
            if (result.isConfirmed) {
                fetch('http://localhost:8080/users/'+userId,{
                    method : 'DELETE'
                }).then((result)=>{
                    return result.json()
                }).then((response)=>{
                    Swal.fire({
                        title : 'User deleted successfully',
                        icon : 'success',
                    })
                     window.location.reload()
                })
            }
          })
       
    }

    const indexOfLastRecord = currentPage * recordsPerPage;
    const indexOfFirstRecord = indexOfLastRecord - recordsPerPage;
    const currentRecords = users.slice(indexOfFirstRecord, indexOfLastRecord);
    const nPages = Math.ceil(users.length / recordsPerPage)

    return (
        <div className="container">
            <div className="card" >
                <div className="card-body">
                    <h5 className="card-title">User's List</h5>
                    <Link to='/user/create' className="btn btn-success" style={{float:'right',margin:'5px'}}>Add User</Link>
                    <table className="table table-striped">
                        <thead className="bg-dark text-white">
                            <tr>
                            <th>S.No</th>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Phone No</th>
                            <th>Action</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                              users &&  users.map((item,index)=>(
                                <tr key={index}>
                                    <td>{index+1}</td>
                                    <td>{item.name}</td>
                                    <td>{item.email}</td>
                                    <td>{item.phone}</td>
                                    <td>
                                        <Link to={`/user/${item.id}`} className="btn btn-sm btn-primary"><i className="fa fa-edit" /></Link>
                                        <button className="btn btn-sm btn-danger" onClick={()=>deleteUser(item.id)}><i className="fa fa-trash" /></button>
                                    </td>
                                </tr>
                                ))
                            }
                        </tbody>
                    </table>
                </div>
                <Pagination
                nPages={nPages}
                currentPage={currentPage}
                setCurrentPage={setCurrentPage}
            />
            </div>

        </div>
    )
}

export default UserList;
