import React, {useEffect, useState} from "react";
import { Modal } from "react-bootstrap";
import { useParams } from "react-router-dom";

const UserEdit = () => {
    const[name,setName] = useState('')
    const[email,setEmail] = useState('')
    const[phone,setPhone] = useState('')
    const {userid} = useParams()

    useEffect(()=>{
        fetch('http://localhost:8080/users/'+id).then((result)=>{
            return result.json()
        }).then((response)=>{
            console.log(response)
        }).catch((err)=>{
            console.log(err.message)
        })
    })

    return (
        <div>
            <Modal show={props.editShow} >

                <Modal.Body>
                    <h6 className="text-success">Edit </h6>
                    <form className="user-form">
                                <div className="mb-3">
                                        <label className="form-label">Name</label>
                                        <input type="text" className="form-control" required name="name" value={name} onChange={e=>setName(e.target.value)} />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Email</label>
                                        <input type="email" className="form-control" required name="email" value={email} onChange={e=>setEmail(e.target.value)} />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Phone</label>
                                        <input type="text" className="form-control" required name="phone" value={phone} onChange={e=>setPhone(e.target.value)} />
                                    </div>

                                    <button type="submit" className="btn btn-success">Submit</button>
                                    <button className="btn btn-danger" onClick={props.closeEdit}>Cancel</button>
                                </form>
                </Modal.Body>
            </Modal>
        </div>
    )
}

export default UserEdit;
