import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Swal from "sweetalert2";

const UserCreate = () => {
    const[name,setName] = useState('')
    const[email,setEmail] = useState('')
    const[phone,setPhone] = useState('')
    const navigate = useNavigate()

    const createUser = (e) =>{
        e.preventDefault()
        const userData = {name,email,phone}

        fetch('http://localhost:8080/users',{
            method : 'POST',
            headers : {
                'Content-Type' : 'application/json',
                'Accept' : 'application/json'
            },
            body : JSON.stringify(userData)
        }).then((result)=>{
            return result.json()
        }).then((response)=>{
            Swal.fire({  
                title: 'User created successfully !',  
                icon: 'success'
              }); 
            navigate('/')
        }).catch((err)=>{
            console.log(err)
        })
    }

    return (
        <div>
            <div className="row">
                <div className="offset-lg-3 col-lg-6">
                    <div className="container">
                        <div className="card" >
                            <div className="card-body">
                                <h5>Add User</h5>
                                <form className="user-form" onSubmit={createUser}>
                                <div className="mb-3">
                                        <label className="form-label">Name</label>
                                        <input type="text" className="form-control" required name="name" value={name} onChange={e=>setName(e.target.value)} />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Email</label>
                                        <input type="email" className="form-control" required name="email" value={email} onChange={e=>setEmail(e.target.value)} />
                                    </div>
                                    <div className="mb-3">
                                        <label className="form-label">Phone</label>
                                        <input type="text" className="form-control" required name="phone" value={phone} onChange={e=>setPhone(e.target.value)} />
                                        {phone && !(phone.match(/^\d[7-8]{1}[1-9]{9}$/))? <span>Enter valid phone number</span>:null}
                                    </div>

                                    <button type="submit" className="btn btn-success">Submit</button>
                                    <Link to="/" className="btn btn-danger">Cancel</Link>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default UserCreate;
